// 示例：金额 > 100 且 (状态 = SUCCESS 或 PARTIAL)
{
  "op": "and",
  "conditions": [
    { "field": "amount", "operator": ">", "value": 100 },
    {
      "op": "or",
      "conditions": [
        { "field": "status", "operator": "=", "value": "SUCCESS" },
        { "field": "status", "operator": "=", "value": "PARTIAL" }
      ]
    }
  ]
